import org.sonatype.nexus.blobstore.api.BlobStoreManager
import org.sonatype.nexus.repository.storage.WritePolicy
def name='helm-hosted'
if(!repository.getRepositoryManager().exists(name)) { def configuration = new Configuration();
configuration.setRepositoryName("helm-hosted-bla");
configuration.setRecipeName("helm-hosted");
configuration.setAttributes(storage: [blobStoreName: BlobStoreManager.DEFAULT_BLOBSTORE_NAME, strictContentTypeValidation: true, writePolicy: WritePolicy.ALLOW_ONCE]);
configuration.setOnline(true);
repository.createRepository(configuration); }
